import React from 'react';
import { NumBtn } from "./components/NumBtn";
import { OperatorBtn } from "./components/OperatorBtn";

export class CalculatorApp extends React.Component {
  constructor(){
    super();
    this.state = {
      displayNum: "",
      inputNum: "",
      operator: "",
    };
  }
  render() {
    return (
      <div>
        <h2>Calculator</h2>
        <NumBtn clickNum={this.clickNum} />
        <OperatorBtn />
      </div>
    );
  }

  clickNum = () => {
    if(this.state.operator === "") {
      this.setState({displayNum: this.state.displayNum + event.target.value});
    } else {
      this.setState({inputNum: this.state.inputNum + event.target.value});
    }
  }
}
