import React from 'react';

export class OperatorBtn extends React.Component {
  constructor() {
    super();
  }
  render() {
    return(
      <div>
        <button onClick={this.clickOperator} value={"+"} className="NumBtn">+</button>
        <button onClick={this.clickOperator} value={"-"} className="NumBtn">-</button>
        <button onClick={this.clickOperator} value={"*"} className="NumBtn">x</button>
        <button onClick={this.clickOperator} value={"/"} className="NumBtn">/</button>
      </div>
    );
  }
  clickOperator = event => {
    event.preventDefault();
    this.props.clickOperator(event.target.value);
  }
}
