import React from 'react';

export class NumBtn extends React.Component {
  constructor(){
    super();
    this.state = {displayNum: 0};
  }

  render(){
    return(
      <div>
        <p>{this.state.displayNum}</p>
        <button onClick={this.puls(1)}>1</button>
        <button onClick={this.puls(2)}>2</button>
        <button onClick={this.puls(3)}>3</button>
        <button onClick={this.puls(4)}>4</button>
        <button onClick={this.puls(5)}>5</button>
        <button onClick={this.puls(6)}>6</button>
        <button onClick={this.puls(7)}>7</button>
        <button onClick={this.puls(8)}>8</button>
        <button onClick={this.puls(9)}>9</button>
      </div>
    )
  }

  puls = (num) => {
    this.setState({displayNum: this.state.displayNum + num});
  }
}
