import React from 'react';

export class NumBtn extends React.Component {
  constructor(){
    super();
    this.state = {
      displayNum: 0,
      inputNum : 0,
      operator: "",
    };
  }

  render(){
    return(
      <div>
        <p>{this.state.displayNum}</p>
        <p>{this.state.inputNum}</p>
        <button onClick={this.input} value={1}>1</button>
        <button onClick={this.input} value={2}>2</button>
        <button onClick={this.input} value={3}>3</button>
        <button onClick={this.input} value={4}>4</button>
        <button onClick={this.input} value={5}>5</button>
        <button onClick={this.input} value={6}>6</button>
        <button onClick={this.input} value={7}>7</button>
        <button onClick={this.input} value={8}>8</button>
        <button onClick={this.input} value={9}>9</button>
        <button onClick={this.onOperator} value={"+"}>+</button>
        <button onClick={this.calulate} value={"+"}>=</button>
      </div>
    )
  }

  input = (event) => {
    this.setState({inputNum: Number(event.target.value)});
  }

  onOperator = (event) => {
    this.state({
      operator: event.target.value
    });
  }
  plus = () => {
    this.setState({
      displayNum: this.state.displayNum + this.state.inputNum
    });
  }
}
